//Write a program to demonstrate usage of different data types of core java 

package corejava.datatypes;

public class DatatypeDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'a';
		int number = 345678912;
		byte b = 84;
		short s = 1023;
		long longnumber = 1231231923121231L;
		float floatingvalue = 3.45F;
		double pi = 3.141592653589793238d;
		boolean condition = false;
		System.out.println(" value of Character type variable ch is " + ch);
		System.out.println(" value of integer type variable number is " + number);
		System.out.println(" value of byte type variable b is " + b);
		System.out.println(" value of short type variable s is " + s);
		System.out.println(" value of long type variable longnumber is " + longnumber);
		System.out.println(" value of float type variable floatingvalue is " + floatingvalue);
		System.out.println(" value of double type variable pi is " + pi);
		System.out.println(" value of boolean type variable condition is " + condition);
	}

}
