// Write a program to demonstrate if selection statement 
package corejava.selectionstatement;

public class IfDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		boolean result = true;
		
		if (result) {
			System.out.println(" This statement is printed in console when condition of if statement is true");
		}
		
		if (!result) {
			System.out.println(" This statement will not printed in console when condition of if statement is true");
		}

	}

}
