//Write a program to display difference of print and println method
package corejava.simple;

public class PrintMethods {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		//Print method 
		System.out.print("Hello World");
		System.out.print(" Greetings from Human of Earth");
		
		//println method 
		System.out.println("\nHello world");
		System.out.println("Greetings from Human of Earth");

	}

}
